interface PointsScreenProps {
 currentPoints?: number;
 onRankChange?: (rank: Rank) => void;
 onNavigateToTaskHistory?: () => void;
}

export function PointsScreen({ currentPoints = 245, onRankChange, onNavigateToTaskHistory }: PointsScreenProps) {
 const [totalEarned] = useState(520);
 const [totalBurned] = useState(275);
 const [debugCurrentRank, setDebugCurrentRank] = useState<Rank | null>(null);

 // Mock data for points growth chart
 const [pointsHistory] = useState([
 { day: 'Mon', points: 180 },
 { day: 'Tue', points: 195 },
 { day: 'Wed', points: 220 },
 { day: 'Thu', points: 210 },
 { day: 'Fri', points: 235 },
 { day: 'Sat', points: 250 },
 { day: 'Sun', points: 245 }
 ]);

 // Task statistics (moved from TaskManagement)
 const [taskStats] = useState({
 totalTasks: 12,
 completedTasks: 8,
 urgentTasks: 3,
 workTasks: 5,
 personalTasks: 4,
 completionRatio: 66.7,
 todayCompleted: 5,
 weeklyAverage: 7.2
 });

 // Use debug rank if set, otherwise calculate from points
 const currentRank = debugCurrentRank || ranks.find(rank => 
 currentPoints >= rank.minPoints && currentPoints <= rank.maxPoints
 ) || ranks[0];

 const nextRank = ranks.find(rank => rank.minPoints > (debugCurrentRank?.minPoints || currentPoints)) || null;

 const progressToNext = nextRank 
 ? ((currentPoints - currentRank.minPoints) / (nextRank.minPoints - currentRank.minPoints)) * 100
 : 100;

 const [activities] = useState<PointActivity[]>([
 { id: '1', type: 'earn', points: 25, activity: 'Completed morning workout', time: '2h ago', icon: Target },
 { id: '2', type: 'burn', points: -10, activity: 'Instagram usage (5min)', time: '3h ago', icon: Smartphone },
 { id: '3', type: 'earn', points: 30, activity: 'Finished reading session', time: '4h ago', icon: Book },
 { id: '4', type: 'burn', points: -15, activity: 'YouTube usage (8min)', time: '5h ago', icon: Smartphone },
 { id: '5', type: 'earn', points: 20, activity: 'Team meeting completed', time: '6h ago', icon: Target },
 ]);

 // Notify parent of rank change for theming
 if (onRankChange) {
 onRankChange(currentRank);
 }

 const handleRankClick = (rank: Rank) => {
 setDebugCurrentRank(rank);
 if (onRankChange) {
 onRankChange(rank);
 }
 };

 // Get stroke color based on rank
 const getStrokeColor = () => {
 if (currentRank.colors.accent.includes('purple')) return '#a855f7';
 if (currentRank.colors.accent.includes('blue')) return '#60a5fa';
 if (currentRank.colors.accent.includes('green')) return '#4ade80';
 if (currentRank.colors.accent.includes('red')) return '#f87171';
 if (currentRank.colors.accent.includes('yellow')) return '#facc15';
 if (currentRank.colors.accent.includes('cyan')) return '#22d3ee';
 if (currentRank.colors.accent.includes('amber')) return '#f59e0b';
 if (currentRank.colors.accent.includes('gray')) return '#9ca3af';
 return '#ffffff';
 };

 // Simple custom chart component
 const CustomChart = ({ data }: { data: { day: string; points: number }[] }) => {
 const maxPoints = Math.max(...data.map(d => d.points));
 const minPoints = Math.min(...data.map(d => d.points));
 const range = maxPoints - minPoints;
 const padding = range * 0.1;
 const adjustedMin = minPoints - padding;
 const adjustedMax = maxPoints + padding;
 const adjustedRange = adjustedMax - adjustedMin;

 const width = 300;
 const height = 120;
 const chartWidth = width - 60;
 const chartHeight = height - 40;

 const points = data.map((item, index) => {
 const x = (index / (data.length - 1)) * chartWidth + 30;
 const y = height - 20 - ((item.points - adjustedMin) / adjustedRange) * chartHeight;
 return { x, y, points: item.points };
 });

 const pathData = points.map((point, index) => 
 `${index === 0 ? 'M' : 'L'} ${point.x} ${point.y}`
 ).join(' ');

 return (
 <div className="w-full h-32 flex items-center justify-center">
 <svg width={width} height={height} className="overflow-visible">
 {/* Grid lines */}
 <defs>
 <pattern id="grid" width="40" height="20" patternUnits="userSpaceOnUse">
 <path d="M 40 0 L 0 0 0 20" fill="none" stroke="#ffffff20" strokeWidth="1" strokeDasharray="2,2"/>
 </pattern>
 </defs>
 <rect width={chartWidth} height={chartHeight} x="30" y="20" fill="url(#grid)" />

 {/* Chart line */}
 <path
 d={pathData}
 fill="none"
 stroke={getStrokeColor()}
 strokeWidth="3"
 strokeLinecap="round"
 strokeLinejoin="round"
 />

 {/* Data points */}
 {points.map((point, index) => (
 <circle
 key={index}
 cx={point.x}
 cy={point.y}
 r="4"
 fill={getStrokeColor()}
 stroke="#000000"
 strokeWidth="2"
 className="cursor-pointer hover:r-6 transition-all"
 />
 ))}

 {/* X-axis labels */}
 {data.map((item, index) => (
 <text
 key={index}
 x={30 + (index / (data.length - 1)) * chartWidth}
 y={height - 5}
 textAnchor="middle"
 fontSize="10"
 fill="#ffffff60"
 >
 {item.day}
 </text>
 ))}
 </svg>
 </div>
 );
 };

 return (
 <div className="h-full bg-black text-white overflow-hidden">
 <Tabs defaultValue="overview" className="h-full flex flex-col">
 <div className={`p-6 border-b ${currentRank.colors.border} ${currentRank.colors.bg}`}>
 <h1 className={`text-xl font-medium mb-4 ${currentRank.colors.accent}`}>POINTS & STATS</h1>
 <TabsList className={`grid w-full grid-cols-3 ${currentRank.colors.bg} ${currentRank.colors.border} border rounded-lg p-1`}>
 <TabsTrigger value="overview" className="text-xs text-white data-[state=active]:bg-white data-[state=active]:text-black">
 OVERVIEW
 </TabsTrigger>
 <TabsTrigger value="tasks" className="text-xs text-white data-[state=active]:bg-white data-[state=active]:text-black">
