 <div className="text-center mb-4">
 <div className="flex items-center justify-center space-x-2 mb-2">
 <currentRank.icon size={32} className={currentRank.colors.accent} />
 <div className={`text-4xl font-medium ${currentRank.colors.accent}`}>
 {currentPoints}
 </div>
 </div>
 <div className="text-xs text-white/60">CURRENT POINTS</div>
 </div>

 <div className="space-y-4">
 <div>
 <div className="flex justify-between text-xs mb-2">
 <span className="text-white/60">CURRENT RANK</span>
 <span className={currentRank.colors.accent}>{currentRank.name}</span>
 </div>
 {nextRank ? (
 <>
 <div className="text-xs text-white/40 mb-3">NEXT: {nextRank.name}</div>
 <Progress 
 value={progressToNext} 
 className={`h-3 ${currentRank.colors.bg}`}
 />
 <div className="text-xs text-white/40 mt-1 text-center">
 {Math.round(progressToNext)}% TO NEXT RANK
 </div>
 </>
 ) : (
 <div className={`text-xs ${currentRank.colors.accent} text-center font-medium`}>MAXIMUM RANK ACHIEVED</div>
