}

interface CustomQuote {
 id: string;
 text: string;
 author: string;
}

interface VisionCard {
 id: string;
 title: string;
 description: string;
 imageUrl: string;
 category: 'intelligence' | 'physical' | 'wealth';
}

interface WhyReason {
 id: string;
 title: string;
 description: string;
}

