'use client';

import { useState, useEffect } from 'react';
import { Button } from './ui/button';
import { Badge } from './ui/badge';
import { Progress } from './ui/progress';
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from './ui/alert-dialog';
import { X, Phone, MessageSquare, Mail, Calendar, Camera, Music, Instagram, Youtube, Twitter, Clock, AlertTriangle, Gamepad2, Facebook, Slack, Chrome, MapPin, Calculator, Settings, FileText, Image, Video, ShoppingCart, Coffee } from 'lucide-react';

interface App {
 id: string;
 name: string;
 icon: any;
 category: 'essential' | 'productivity' | 'social' | 'entertainment' | 'games' | 'shopping';
 pointCost: number;
 description: string;
}

interface AppDrawerProps {
 onClose: () => void;
 rankColors?: any;
}

export function AppDrawer({ onClose, rankColors = { accent: 'text-white', border: 'border-white/20', bg: 'bg-white/10' } }: AppDrawerProps) {
 const [countdown, setCountdown] = useState(60);
 const [isUnlocked, setIsUnlocked] = useState(false);
 const [selectedApp, setSelectedApp] = useState<App | null>(null);
 const [showWarning, setShowWarning] = useState(false);

 const apps: App[] = [
