 { id: '3', name: 'Mail', icon: Mail, category: 'essential', pointCost: 0, description: 'Email communication' },
 { id: '4', name: 'Calendar', icon: Calendar, category: 'essential', pointCost: 0, description: 'Schedule management' },

 // Productivity Apps
 { id: '5', name: 'Camera', icon: Camera, category: 'productivity', pointCost: 0, description: 'Take photos and videos' },
 { id: '6', name: 'Maps', icon: MapPin, category: 'productivity', pointCost: 0, description: 'Navigation and location' },
 { id: '7', name: 'Calculator', icon: Calculator, category: 'productivity', pointCost: 0, description: 'Basic calculations' },
 { id: '8', name: 'Files', icon: FileText, category: 'productivity', pointCost: 0, description: 'File management' },
 { id: '9', name: 'Notes', icon: FileText, category: 'productivity', pointCost: 0, description: 'Quick notes' },
