 if (countdown > 0) {
 const timer = setTimeout(() => setCountdown(countdown - 1), 1000);
 return () => clearTimeout(timer);
 } else {
 setIsUnlocked(true);
 }
 }, [countdown]);

 const handleAppClick = (app: App) => {
 if (app.pointCost > 0) {
 setSelectedApp(app);
 setShowWarning(true);
 } else {
 // Open app (simulation)
 console.log(`Opening ${app.name}`);
 onClose();
 }
