 case 'essential': return 'text-green-400';
 case 'productivity': return 'text-blue-400';
 case 'social': return 'text-red-400';
 case 'entertainment': return 'text-yellow-400';
 case 'games': return 'text-purple-400';
 case 'shopping': return 'text-orange-400';
 default: return 'text-white/60';
