 games: apps.filter(app => app.category === 'games'),
 shopping: apps.filter(app => app.category === 'shopping'),
 };

 return (
 <div className="h-full bg-black text-white flex flex-col">
 {/* Header */}
 <div className={`flex items-center justify-between p-4 border-b ${rankColors.border} ${rankColors.bg}`}>
