 <div className="p-4 bg-yellow-400/10 border-b border-yellow-400/20">
 <div className="flex items-center space-x-2">
 <AlertTriangle size={16} className="text-yellow-400" />
 <span className="text-sm text-yellow-400 font-medium">POINT BURN ZONE</span>
 </div>
 <p className="text-xs text-white/60 mt-1">
 Distraction apps consume points. Choose wisely.
 </p>
 </div>

 {/* App Categories */}
 <div className="flex-1 p-4 overflow-y-auto space-y-6">
 {Object.entries(categorizedApps).map(([category, categoryApps]) => (
 <div key={category}>
 <div className="flex items-center justify-between mb-3">
 <h3 className={`text-sm font-medium uppercase tracking-wider ${getCategoryColor(category)}`}>
 {category}
 </h3>
 <Badge variant="outline" className={`text-xs ${getCategoryColor(category)} border-current`}>
 {categoryApps.length} apps
 </Badge>
