 >
 <app.icon size={20} className="text-white" />
 {app.pointCost > 0 && (
 <Badge 
 variant="outline" 
 className={`absolute -top-1 -right-1 text-xs scale-75 ${getPointCostColor(app.pointCost)}`}
 >
 -{app.pointCost}
 </Badge>
 )}
 </button>
 <div className="text-xs text-white/80 font-medium mb-1">{app.name}</div>
 <div className="text-xs text-white/40 leading-tight">
