@Composable
fun TaskList(tasks: List<Task>, taskRepo: TaskRepository, onTaskCompleted: (String) -> Unit) {
 LazyColumn(
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 16.dp)
 .weight(1f),
 verticalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 if (tasks.isEmpty()) {
 item {
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 32.dp),
 contentAlignment = Alignment.Center
 ) {
 Text("No tasks yet. Add a task to get started!", style = MaterialTheme.typography.bodyLarge)
 }
 }
 } else {
 items(tasks) { task ->
 TaskItem(task = task, onTaskCompleted = onTaskCompleted)
 }
 }
 }
}

@Composable
