@Composable
fun HomeScreen(navController: NavController) {
 val context = LocalContext.current
 val appRepository = remember { AppRepository(context) }
 val taskRepository = remember { TaskRepository() }
 val apps = remember { mutableStateOf<List<AppInfo>>(emptyList()) }
 val tasks = remember { mutableStateOf<List<Task>>(emptyList()) }
 var showAddTaskDialog by remember { mutableStateOf(false) }

 LaunchedEffect(Unit) {
 apps.value = appRepository.getInstalledApps().filter {
 it.name in listOf("Phone", "Messages") || it.name.contains("App")
 }.take(3)
 }

 LaunchedEffect(taskRepository) {
 tasks.value = taskRepository.getAllTasks()
 }

 Scaffold(
 bottomBar = { BottomNavigationBar() },
 containerColor = MaterialTheme.colorScheme.background
 ) {
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(it),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 PointsHeader(currentPoints = 245, pointChange = 15, completed = 0, total = 2)
 Spacer(modifier = Modifier.height(8.dp))
 Divider(modifier = Modifier.fillMaxWidth(), color = DividerGray)
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .weight(1f)
 ) {
 TaskList(tasks.value, taskRepository, onTaskCompleted = { id ->
 taskRepository.completeTask(id)
 tasks.value = taskRepository.getAllTasks()
 })
 }
 AddTaskBar { showAddTaskDialog = true }
 TunnelVisionBanner()
 AppShortcutBar(onAppClick = { appName -> /* TODO: Launch intent */ })
 }
 if (showAddTaskDialog) {
 TaskDialog(
 onDismiss = { showAddTaskDialog = false },
 onTaskAdded = { title, desc ->
 taskRepository.addTask(title, desc) // TODO: support new fields
 tasks.value = taskRepository.getAllTasks()
 }
 )
 }
 }
}

@Composable
fun PointsHeader(currentPoints: Int, pointChange: Int, completed: Int, total: Int) {
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(24.dp),
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.SpaceBetween
 ) {
 Row(verticalAlignment = Alignment.CenterVertically) {
