 singleLine = true,
 keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number)
 )
 if(points > 30) {
 Text("Max 30 points allowed.", color = AccentGreen)
 }
 }
 },
 confirmButton = {
 TextButton(
 onClick = {
 if (title.isNotBlank() && points in 0..30) {
 onTaskAdded(title, description, points)
 onDismiss()
 }
 },
 modifier = Modifier.background(Color.White)
 ) {
 Text("Add Task", color = Color.Black)
 }
 },
 dismissButton = {
 TextButton(onClick = onDismiss, modifier = Modifier.background(SurfaceCard)) {
 Text("Cancel", color = Color.White)
 }
 },
 containerColor = SurfaceCard
 )
}
