import { ArrowLeft, ExternalLink, Play, FileText, Link as LinkIcon, Trophy } from 'lucide-react';

interface TaskLink {
 id: string;
 url: string;
 title: string;
 type: 'article' | 'video' | 'link';
 thumbnail?: string;
 description?: string;
}

interface TaskDetailsProps {
 task: {
 id: string;
 text: string;
 description: string;
 completed: boolean;
 category: 'URGENT' | 'IMPORTANT' | 'PERSONAL' | 'WORK';
 priority: number;
 points: number;
 links: TaskLink[];
 };
 onBack: () => void;
 onToggle: (id: string) => void;
 rankColors: any;
}

export function TaskDetails({ task, onBack, onToggle, rankColors }: TaskDetailsProps) {
 const [expandedLink, setExpandedLink] = useState<string | null>(null);

 const getLinkIcon = (type: string) => {
 switch (type) {
 case 'video': return <Play size={14} />;
 case 'article': return <FileText size={14} />;
 default: return <LinkIcon size={14} />;
 }
 };

 const openLink = (url: string) => {
 // Simulate opening link
 console.log(`Opening: ${url}`);
 };

 return (
 <div className="h-full bg-black text-white flex flex-col">
 {/* Header */}
 <div className="p-6 border-b border-white/20">
 <div className="flex items-center space-x-4 mb-4">
 <Button
 onClick={onBack}
 variant="ghost"
 size="sm"
 className="p-2 text-white/60 hover:text-white hover:bg-white/10"
 >
 <ArrowLeft size={20} />
 </Button>
 <h1 className="text-lg font-medium">TASK DETAILS</h1>
 </div>

 {/* Points Display */}
 <div className="flex items-center justify-center mb-4">
 <div className={`border-2 ${rankColors.border} rounded-lg p-4 text-center`}>
 <div className="flex items-center justify-center space-x-2 mb-2">
 <Trophy size={20} className={rankColors.accent} />
 <span className={`text-2xl font-medium ${rankColors.accent}`}>
 {task.points}
 </span>
 </div>
 <div className="text-xs text-white/60">POINTS REWARD</div>
 </div>
 </div>
 </div>

 {/* Content */}
 <div className="flex-1 p-6 overflow-y-auto space-y-6">
 {/* Task Info */}
 <div className="border border-white/20 rounded-lg p-4">
 <div className="flex items-start space-x-4 mb-4">
 <Checkbox
 checked={task.completed}
 onCheckedChange={() => onToggle(task.id)}
 className="h-5 w-5 border-white/40 data-[state=checked]:bg-white data-[state=checked]:text-black mt-1"
 />
 <div className="flex-1">
 <h2 className={`font-medium mb-2 ${task.completed ? 'line-through text-white/50' : 'text-white'}`}>
 {task.text}
 </h2>
 <div className="flex items-center space-x-2 mb-3">
 <Badge variant="outline" className="text-xs border-white/40 text-white/60">
 {task.category}
 </Badge>
 <Badge variant="outline" className="text-xs border-white/40 text-white/60">
 PRIORITY {task.priority}
 </Badge>
 </div>
 <p className="text-white/70 text-sm leading-relaxed">
 {task.description}
 </p>
 </div>
 </div>
 </div>

 {/* Links Section */}
 {task.links.length > 0 && (
 <div className="border border-white/20 rounded-lg p-4">
 <h3 className="font-medium mb-4 flex items-center">
 <LinkIcon size={16} className="mr-2" />
 RESOURCES & LINKS
 </h3>

 <div className="space-y-3">
 {task.links.map((link) => (
 <div key={link.id} className="border border-white/10 rounded-lg p-3 hover:border-white/30 transition-colors">
 <div className="flex items-start space-x-3">
 {/* Thumbnail */}
 <div className="w-16 h-12 bg-white/10 rounded-lg flex items-center justify-center flex-shrink-0">
 {link.thumbnail ? (
 <img 
 src={link.thumbnail} 
 alt={link.title}
 className="w-full h-full object-cover rounded-lg"
 />
 ) : (
 getLinkIcon(link.type)
 )}
 </div>

 {/* Content */}
 <div className="flex-1 min-w-0">
 <div className="flex items-start justify-between">
 <div className="flex-1 min-w-0">
 <h4 className="text-sm font-medium text-white truncate">
 {link.title}
 </h4>
 <div className="flex items-center space-x-2 mt-1">
 <Badge variant="outline" className="text-xs border-white/30 text-white/50">
 {link.type.toUpperCase()}
 </Badge>
 </div>
 {link.description && expandedLink === link.id && (
 <p className="text-xs text-white/60 mt-2 leading-relaxed">
 {link.description}
 </p>
 )}
 </div>

 <Button
 onClick={() => openLink(link.url)}
 variant="ghost"
 size="sm"
 className="text-white/60 hover:text-white p-1 h-auto ml-2"
 >
 <ExternalLink size={14} />
 </Button>
 </div>

 {link.description && (
 <Button
 onClick={() => setExpandedLink(expandedLink === link.id ? null : link.id)}
 variant="ghost"
 size="sm"
 className="text-white/40 hover:text-white/60 p-0 h-auto text-xs mt-1"
 >
 {expandedLink === link.id ? 'Show less' : 'Show more'}
 </Button>
 )}
 </div>
 </div>
 </div>
 ))}
 </div>
 </div>
 )}

 {/* Completion Status */}
 <div className={`border rounded-lg p-4 text-center ${
 task.completed 
 ? 'border-green-400/40 bg-green-400/10' 
 : 'border-white/20'
 }`}>
 <div className={`text-sm ${task.completed ? 'text-green-400' : 'text-white/60'}`}>
 {task.completed 
 ? '✓ TASK COMPLETED - POINTS EARNED' 
 : 'COMPLETE THIS TASK TO EARN POINTS'}
 </div>
 </div>
 </div>
 </div>
 );
}




'use client';

import { Crown, Star, Zap, Target, Trophy, CheckCircle2 } from 'lucide-react';
import { Badge } from './ui/badge';

interface TasksCompletedCelebrationProps {
 rankColors?: any;
 completedCount?: number;
 pointsEarned?: number;
}

export function TasksCompletedCelebration({ 
 rankColors = { accent: 'text-white', border: 'border-white/20', bg: 'bg-white/10' },
 completedCount = 5,
 pointsEarned = 125
}: TasksCompletedCelebrationProps) {
 const floatingIcons = [
 { icon: Star, delay: '0s', size: 16, position: 'top-4 left-8' },
 { icon: Crown, delay: '0.5s', size: 20, position: 'top-12 right-12' },
 { icon: Zap, delay: '1s', size: 14, position: 'top-20 left-16' },
 { icon: Trophy, delay: '1.5s', size: 18, position: 'top-8 right-6' },
 { icon: Target, delay: '2s', size: 16, position: 'top-16 left-4' },
 { icon: CheckCircle2, delay: '2.5s', size: 14, position: 'top-6 right-20' }
 ];

 return (
 <div className="flex flex-col items-center justify-center min-h-96 p-8 relative overflow-hidden">
 {/* Background gradient */}
 <div className={`absolute inset-0 ${rankColors.bg} opacity-20`} />

 {/* Floating celebration icons */}
 {floatingIcons.map((item, index) => (
 <div
 key={index}
 className={`absolute ${item.position} animate-bounce opacity-60`}
 style={{ 
 animationDelay: item.delay,
 animationDuration: '2s',
 animationIterationCount: 'infinite'
 }}
 >
 <item.icon size={item.size} className={rankColors.accent} />
 </div>
 ))}

 {/* Central celebration content */}
 <div className="text-center z-10 space-y-6">
 {/* Main celebration icon */}
 <div className={`relative mx-auto w-24 h-24 rounded-full ${rankColors.bg} border-2 ${rankColors.border} flex items-center justify-center`}>
 <CheckCircle2 size={48} className={`${rankColors.accent} animate-pulse`} />

 {/* Pulsing ring effect */}
 <div className={`absolute inset-0 rounded-full border-2 ${rankColors.border} animate-ping`} />
 </div>

 {/* Celebration text */}
 <div className="space-y-3">
 <h2 className={`text-2xl font-bold ${rankColors.accent} tracking-wide`}>
 ALL TASKS COMPLETED
 </h2>

 <p className="text-white/70 text-lg">
 Outstanding discipline and focus!
 </p>

 <div className="flex items-center justify-center space-x-4 mt-4">
 <Badge 
 variant="outline" 
 className={`${rankColors.border} ${rankColors.accent} bg-transparent px-4 py-2 text-sm`}
 >
 {completedCount} Tasks Done
 </Badge>

 <Badge 
 variant="outline" 
 className="border-green-400/40 text-green-400 bg-transparent px-4 py-2 text-sm"
 >
 +{pointsEarned} Points
 </Badge>
 </div>
 </div>

 {/* Motivational quote */}
 <div className={`border-l-4 ${rankColors.border} pl-4 py-3 bg-black/20 rounded-r-lg max-w-sm mx-auto`}>
 <p className="text-white/80 text-sm font-medium italic">
 "Excellence is not a skill, it's an attitude. Today you've proven yours."
 </p>
 </div>

 {/* Achievement badges */}
 <div className="flex justify-center space-x-2 mt-6">
 <div className={`p-3 rounded-lg ${rankColors.bg} border ${rankColors.border}`}>
 <Trophy size={20} className="text-yellow-400" />
 </div>
 <div className={`p-3 rounded-lg ${rankColors.bg} border ${rankColors.border}`}>
 <Crown size={20} className={rankColors.accent} />
 </div>
 <div className={`p-3 rounded-lg ${rankColors.bg} border ${rankColors.border}`}>
 <Star size={20} className="text-white" />
 </div>
 </div>

 {/* Next steps suggestion */}
 <div className="text-center mt-8 space-y-2">
 <p className="text-white/50 text-sm">
 Ready for tomorrow's challenges?
 </p>
 <p className="text-white/40 text-xs">
 Your consistency builds your legacy
 </p>
 </div>
 </div>

 {/* Subtle background pattern */}
 <div className="absolute inset-0 opacity-5">
 <div className={`w-full h-full ${rankColors.accent.replace('text-', 'bg-')} opacity-10`} 
 style={{
 backgroundImage: `radial-gradient(circle at 2px 2px, currentColor 1px, transparent 0)`,
 backgroundSize: '20px 20px'
 }} 
 />
 </div>
 </div>
 );
}



'use client';

import React, { useState } from 'react';
import { Plus, ChevronRight, Brain, Zap, DollarSign, ToggleLeft, ToggleRight, Edit3, Check, X, TrendingUp, TrendingDown, Trophy, Grid3x3, Phone, MessageSquare } from 'lucide-react';
import { Checkbox } from './ui/checkbox';
import { Button } from './ui/button';
import { Input } from './ui/input';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from './ui/dialog';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
import { TaskDetails } from './TaskDetails';
import { TasksCompletedCelebration } from './TasksCompletedCelebration';
import { EssentialAppsDrawer } from './EssentialAppsDrawer';

interface TaskLink {
 id: string;
 url: string;
 title: string;
 type: 'article' | 'video' | 'link';
 thumbnail?: string;
 description?: string;
}

interface Task {
 id: string;
 text: string;
 description: string;
 completed: boolean;
 category: 'URGENT' | 'IMPORTANT' | 'PERSONAL' | 'WORK' | 'INTELLIGENCE' | 'PHYSICAL' | 'WEALTH';
 priority: number;
 points: number;
 links: TaskLink[];
}

interface FocusCategory {
 id: string;
 name: string;
 icon: any;
 color: string;
 bgColor: string;
 borderColor: string;
}

interface HomeScreenProps {
 rankColors?: any;
 onNavigateToPoints?: () => void;
 hideCompletedTasks?: boolean;
 onNavigateToTaskHistory?: () => void;
 onNavigateToAppDrawer?: () => void;
}

export function HomeScreen({ 
 rankColors = { accent: 'text-white', border: 'border-white/20' }, 
 onNavigateToPoints,
 hideCompletedTasks = true,
 onNavigateToTaskHistory,
 onNavigateToAppDrawer
}: HomeScreenProps) {
 const [mode, setMode] = useState<'simple' | 'focused'>('simple');
 const [selectedTaskId, setSelectedTaskId] = useState<string | null>(null);
 const [showAddTaskDialog, setShowAddTaskDialog] = useState(false);
 const [newTaskText, setNewTaskText] = useState('');
 const [newTaskDescription, setNewTaskDescription] = useState('');
 const [newTaskCategory, setNewTaskCategory] = useState<string>('INTELLIGENCE');
 const [newTaskPoints, setNewTaskPoints] = useState(20);
 const [editingCategoryId, setEditingCategoryId] = useState<string | null>(null);
 const [editingCategoryName, setEditingCategoryName] = useState('');
 const [showEssentialApps, setShowEssentialApps] = useState(false);

 // Points system state
 const [currentPoints] = useState(245);
 const [pointsTrend] = useState<'increasing' | 'decreasing'>('increasing'); // This would be calculated from recent activities
 const [pointsChange] = useState(15); // Recent change in points

 const [focusCategories, setFocusCategories] = useState<FocusCategory[]>([
 {
 id: 'INTELLIGENCE',
 name: 'Becoming Incredibly Intelligent',
 icon: Brain,
 color: 'text-blue-400',
 bgColor: 'bg-blue-400/10',
 borderColor: 'border-blue-400/30'
 },
 {
 id: 'PHYSICAL',
 name: 'Becoming Incredibly Muscular',
 icon: Zap,
 color: 'text-orange-400',
 bgColor: 'bg-orange-400/10',
 borderColor: 'border-orange-400/30'
 },
 {
 id: 'WEALTH',
 name: 'Becoming Incredibly Rich',
 icon: DollarSign,
 color: 'text-green-400',
 bgColor: 'bg-green-400/10',
 borderColor: 'border-green-400/30'
 }
 ]);

 const [tasks, setTasks] = useState<Task[]>([
 // Simple mode tasks
 { 
 id: '1', 
 text: 'Complete morning workout', 
 description: 'A comprehensive 45-minute workout routine focusing on strength training and cardio to boost energy and productivity for the day.',
 completed: false, 
 category: 'PERSONAL', 
 priority: 1,
 points: 25,
 links: [
 {
 id: 'l1',
 url: 'https://youtube.com/watch?v=workout',
 title: 'Morning Strength Routine',
 type: 'video',
 thumbnail: 'https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?w=200&h=120&fit=crop',
 description: 'A 20-minute strength training routine perfect for mornings'
 }
 ]
 },
 { 
 id: '2', 
 text: 'Review project proposal', 
 description: 'Thoroughly review the Q4 project proposal, analyze budget requirements, timeline feasibility, and resource allocation.',
 completed: false, 
 category: 'WORK', 
 priority: 2,
 points: 40,
 links: [
 {
 id: 'l2',
 url: 'https://docs.google.com/proposal',
 title: 'Q4 Project Proposal Draft',
 type: 'article',
 thumbnail: 'https://images.unsplash.com/photo-1554224155-8d04cb21cd6c?w=200&h=120&fit=crop',
 description: 'Complete project proposal with timeline and budget'
 }
 ]
 },
 // Focused mode tasks
 {
 id: '3',
 text: 'Read 30 pages of cognitive science book',
 description: 'Study advanced cognitive psychology concepts to enhance thinking patterns and mental models.',
 completed: false,
 category: 'INTELLIGENCE',
 priority: 1,
 points: 35,
 links: []
 },
 {
 id: '4',
 text: 'Complete strength training session',
 description: 'Full body workout focusing on compound movements: deadlifts, squats, bench press, and pull-ups.',
 completed: true,
 category: 'PHYSICAL',
 priority: 1,
 points: 30,
 links: []
 },
 {
 id: '5',
 text: 'Research investment opportunities',
 description: 'Analyze potential investment opportunities in emerging markets and cryptocurrency.',
 completed: false,
 category: 'WEALTH',
 priority: 1,
 points: 40,
 links: []
 }
 ]);

 const toggleTask = (id: string) => {
 setTasks(tasks.map(task => 
 task.id === id ? { ...task, completed: !task.completed } : task
 ));
 };

 const addTask = () => {
 if (newTaskText.trim()) {
 const categoryForNewTask = mode === 'focused' ? newTaskCategory : 'PERSONAL';
 const newTask: Task = {
 id: Date.now().toString(),
 text: newTaskText,
 description: newTaskDescription || 'New task description',
 completed: false,
 category: categoryForNewTask as any,
 priority: tasks.length + 1,
 points: newTaskPoints,
 links: []
 };
 setTasks([...tasks, newTask]);

 // Reset form
 setNewTaskText('');
 setNewTaskDescription('');
 setNewTaskPoints(20);
 setShowAddTaskDialog(false);
 }
 };

 const startEditingCategory = (categoryId: string) => {
 const category = focusCategories.find(cat => cat.id === categoryId);
 if (category) {
 setEditingCategoryId(categoryId);
 setEditingCategoryName(category.name);
 }
 };

 const saveEditingCategory = () => {
 if (editingCategoryId && editingCategoryName.trim()) {
 setFocusCategories(focusCategories.map(cat => 
 cat.id === editingCategoryId 
 ? { ...cat, name: editingCategoryName.trim() }
 : cat
 ));
 setEditingCategoryId(null);
 setEditingCategoryName('');
 }
 };

 const cancelEditingCategory = () => {
 setEditingCategoryId(null);
 setEditingCategoryName('');
 };

 const getSimpleTasks = () => tasks.filter(task => 
 ['URGENT', 'IMPORTANT', 'PERSONAL', 'WORK'].includes(task.category)
 );

 const getFocusedTasks = (categoryId: string) => tasks.filter(task => 
 task.category === categoryId
 );

 const getTasksForMode = () => {
 if (mode === 'simple') {
 return getSimpleTasks();
 }
 return tasks.filter(task => 
 ['INTELLIGENCE', 'PHYSICAL', 'WEALTH'].includes(task.category)
 );
 };

 // Filter tasks based on hideCompletedTasks setting
 const getFilteredTasksForMode = () => {
 const allTasks = getTasksForMode();
 if (hideCompletedTasks) {
 return allTasks.filter(task => !task.completed);
 }
 return allTasks;
 };

 const allTasks = getTasksForMode();
 const filteredTasks = getFilteredTasksForMode();
 const completedCount = allTasks.filter(task => task.completed).length;
 const totalTasks = allTasks.length;
 const allTasksCompleted = totalTasks > 0 && completedCount === totalTasks;

 // Calculate points earned from completed tasks
 const pointsEarned = allTasks.filter(task => task.completed).reduce((sum, task) => sum + task.points, 0);

 // If a task is selected, show task details
 const selectedTask = tasks.find(task => task.id === selectedTaskId);
 if (selectedTask) {
 return (
 <TaskDetails
 task={selectedTask}
 onBack={() => setSelectedTaskId(null)}
 onToggle={toggleTask}
 rankColors={rankColors}
 />
 );
 }

 // Quick access essential apps
 const essentialApps = [
 { name: 'Phone', icon: Phone, action: () => console.log('Opening Phone') },
 { name: 'Messages', icon: MessageSquare, action: () => console.log('Opening Messages') },
 ];

 const handleEssentialAppClick = (appAction: () => void) => {
 appAction();
 };

 const handleOpenFullApps = () => {
 setShowEssentialApps(false);
 onNavigateToAppDrawer?.();
 };

 return (
 <div className="h-full bg-black text-white flex flex-col relative">
 {/* Header with Mode Toggle - Fixed */}
 <div className="p-6 flex-shrink-0">
 <div className="flex justify-between items-start mb-4">
 <div 
 className="cursor-pointer"
 onClick={() => {
 // Navigate to points page - this would be handled by parent component
 onNavigateToPoints?.();
 }}
 >
 <div className="flex items-center space-x-2">
 <Trophy size={20} className={rankColors.accent} />
 <h1 className={`text-2xl font-medium tracking-tight ${rankColors.accent}`}>
 {currentPoints}
 </h1>
 <div className={`flex items-center ${pointsTrend === 'increasing' ? 'text-green-400' : 'text-red-400'}`}>
 {pointsTrend === 'increasing' ? (
 <TrendingUp size={16} />
 ) : (
 <TrendingDown size={16} />
 )}
 <span className="text-sm ml-1">
 {pointsTrend === 'increasing' ? '+' : ''}{pointsChange}
 </span>
 </div>
 </div>
 <p className="text-white/60 text-sm">CURRENT POINTS</p>
 </div>
 <div className="text-right">
 <div className={`text-lg font-medium ${rankColors.accent}`}>{completedCount}/{totalTasks}</div>
 <div className="text-xs text-white/60">COMPLETED</div>
 </div>
 </div>

 {/* Mode Toggle */}
 <div className="flex items-center justify-center mb-4">
 <Button
 onClick={() => setMode(mode === 'simple' ? 'focused' : 'simple')}
 variant="ghost"
 className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-colors ${
 mode === 'simple' 
 ? 'bg-white/10 text-white' 
 : `${rankColors.bg} ${rankColors.accent}`
 }`}
 >
 {mode === 'simple' ? <ToggleLeft size={20} /> : <ToggleRight size={20} />}
 <span className="text-sm font-medium">
 {mode === 'simple' ? 'SIMPLE MODE' : 'FOCUSED MODE'}
 </span>
 </Button>
 </div>

 {/* Enhanced Progress Bar */}
 <div className={`w-full bg-white/20 h-2 rounded-full ${rankColors.bg}`}>
 <div 
 className={`h-2 rounded-full transition-all duration-300 ${rankColors.accent.replace('text-', 'bg-')}`}
 style={{ width: `${totalTasks > 0 ? (completedCount / totalTasks) * 100 : 0}%` }}
 />
 </div>
 </div>

 {/* Scrollable Task Content */}
 <div className="flex-1 overflow-y-auto px-6">
 {allTasksCompleted ? (
 <TasksCompletedCelebration 
 rankColors={rankColors}
 completedCount={completedCount}
 pointsEarned={pointsEarned}
 />
 ) : (
 <>
 {mode === 'simple' ? (
 <SimpleMode 
 tasks={filteredTasks.filter(task => 
 ['URGENT', 'IMPORTANT', 'PERSONAL', 'WORK'].includes(task.category)
 )}
 onTaskClick={setSelectedTaskId}
 onToggleTask={toggleTask}
 rankColors={rankColors}
 hideCompletedTasks={hideCompletedTasks}
 />
 ) : (
 <FocusedMode 
 categories={focusCategories}
 tasks={filteredTasks}
 onTaskClick={setSelectedTaskId}
 onToggleTask={toggleTask}
 rankColors={rankColors}
 onEditCategory={startEditingCategory}
 editingCategoryId={editingCategoryId}
 editingCategoryName={editingCategoryName}
 onSaveEdit={saveEditingCategory}
 onCancelEdit={cancelEditingCategory}
 onEditingNameChange={setEditingCategoryName}
 hideCompletedTasks={hideCompletedTasks}
 />
 )}
 </>
 )}
 </div>

 {/* Simple Mode Add Task Section */}
 {mode === 'simple' && (
 <div className={`p-6 pt-4 border-t ${rankColors.border} flex-shrink-0`}>
 <Button
 onClick={() => setShowAddTaskDialog(true)}
 variant="ghost"
 className={`w-full justify-start text-white/60 hover:${rankColors.accent} hover:${rankColors.bg} p-4 h-auto`}
 >
 <Plus size={20} className="mr-3" />
 ADD TASK
 </Button>
 </div>
 )}

 {/* Motivational Quote Section */}
 <div className="p-4 flex-shrink-0">
 <div className="text-center">
 <div className={`text-xs ${rankColors.accent} font-medium tracking-wide opacity-90 mb-1`}>
 "Discipline is the bridge between goals and accomplishment."
 </div>
 <div className="text-xs text-white/50">
 — Jim Rohn
 </div>
 </div>
 </div>

 {/* Essential Apps Bar */}
 <div className={`p-4 border-t ${rankColors.border} bg-black/80 backdrop-blur-sm flex-shrink-0`}>
 <div className="flex items-center justify-between">
 {/* Left Essential App */}
 <button
 onClick={() => handleEssentialAppClick(essentialApps[0].action)}
 className={`w-12 h-12 bg-white/10 rounded-xl flex items-center justify-center border ${rankColors.border} hover:bg-white/20 transition-all duration-200 hover:scale-105`}
 >
 {React.createElement(essentialApps[0].icon, { size: 20, className: "text-white" })}
 </button>

 {/* Center App Drawer */}
 <button
 onClick={() => setShowEssentialApps(true)}
 className={`w-14 h-14 bg-white/10 rounded-xl flex items-center justify-center border-2 ${rankColors.border} hover:${rankColors.bg} transition-all duration-200 hover:scale-105 relative`}
 >
 <Grid3x3 size={22} className={rankColors.accent} />
 <div className="absolute -top-1 -right-1 w-3 h-3 bg-green-400 rounded-full"></div>
 </button>

 {/* Right Essential App */}
 <button
 onClick={() => handleEssentialAppClick(essentialApps[1].action)}
 className={`w-12 h-12 bg-white/10 rounded-xl flex items-center justify-center border ${rankColors.border} hover:bg-white/20 transition-all duration-200 hover:scale-105`}
 >
 {React.createElement(essentialApps[1].icon, { size: 20, className: "text-white" })}
 </button>
 </div>

 {/* Essential Apps Labels */}
 <div className="flex items-center justify-between mt-2">
 <span className="text-xs text-white/40 text-center w-12">{essentialApps[0].name}</span>
 <span className={`text-xs ${rankColors.accent} font-medium text-center w-14`}>APPS</span>
 <span className="text-xs text-white/40 text-center w-12">{essentialApps[1].name}</span>
 </div>
 </div>

 {/* Floating Action Button for Focused Mode */}
 {mode === 'focused' && (
 <Button
 onClick={() => setShowAddTaskDialog(true)}
 className={`fixed bottom-36 right-6 w-14 h-14 rounded-full ${rankColors.accent.replace('text-', 'bg-')} text-black hover:scale-110 transition-transform shadow-2xl z-50 border-2 border-black/20`}
 >
 <Plus size={24} />
 </Button>
 )}

 {/* Enhanced Focus Mode Indicator */}
 <div className="text-center p-4 flex-shrink-0">
 <div className={`text-xs ${rankColors.accent} tracking-wider opacity-80`}>
 {mode === 'simple' ? 'TUNNEL VISION MODE ACTIVE' : 'FOCUSED DEVELOPMENT MODE ACTIVE'}
 </div>
 <div className="text-xs text-white/60 mt-1">
 {mode === 'simple' ? 'Stay focused. Complete your goals.' : 'Transform yourself through disciplined action.'}
 </div>
 </div>
 </div>
 );
}

function SimpleMode({ tasks, onTaskClick, onToggleTask, rankColors }: any) {
 const getLinkIcon = (type: string) => {
 switch (type) {
 case 'video': return <span className="text-white/60"></span>;
 case 'article': return <span className="text-white/60"></span>;
 default: return <span className="text-white/60"></span>;
 }
 };

 return (
 <div className="space-y-4 pb-6">
 {tasks.map((task: any) => (
 <div key={task.id} className="group">
 <div className="flex items-start space-x-4">
 <Checkbox
 checked={task.completed}
 onCheckedChange={() => onToggleTask(task.id)}
 className={`h-5 w-5 border-white/40 data-[state=checked]:${rankColors.accent.replace('text-', 'bg-')} data-[state=checked]:text-black mt-1 flex-shrink-0`}
 />

 <div className="flex-1 min-w-0">
 <div 
 onClick={() => onTaskClick(task.id)}
 className="cursor-pointer"
 >
 <div className={`${task.completed ? 'line-through text-white/50' : 'text-white'} mb-2`}>
 {task.text}
 </div>

 <div className="flex items-center justify-between text-xs mb-2">
 <span className={`${rankColors.accent} font-medium`}>+{task.points} pts</span>
 </div>

 {/* Show links only in simple mode if they exist */}
 {task.links.length > 0 && (
 <div className="flex space-x-2 mt-2">
 {task.links.slice(0, 2).map((link: any) => (
 <div 
 key={link.id}
 className={`flex items-center space-x-1 ${rankColors.bg} ${rankColors.border} border rounded px-2 py-1 text-xs hover:${rankColors.accent.replace('text-', 'bg-')}/20 transition-colors`}
 onClick={(e) => {
 e.stopPropagation();
 console.log(`Opening: ${link.url}`);
 }}
 >
 {link.thumbnail ? (
 <img 
 src={link.thumbnail} 
 alt={link.title}
 className="w-4 h-3 object-cover rounded"
 />
 ) : (
 getLinkIcon(link.type)
 )}
 <span className="text-white/60 truncate max-w-20">
 {link.title}
 </span>
 </div>
 ))}
 {task.links.length > 2 && (
 <div className="flex items-center text-xs text-white/40">
 +{task.links.length - 2} more
 </div>
 )}
 </div>
 )}
 </div>
 </div>

 <ChevronRight 
 size={16} 
 className={`text-white/40 opacity-0 group-hover:opacity-100 group-hover:${rankColors.accent} transition-all cursor-pointer mt-1 flex-shrink-0`}
 onClick={() => onTaskClick(task.id)}
 />
 </div>
 </div>
 ))}
 </div>
 );
}

function FocusedMode({ 
 categories, 
 tasks, 
 onTaskClick, 
 onToggleTask, 
 rankColors, 
 onEditCategory,
 editingCategoryId,
 editingCategoryName,
 onSaveEdit,
 onCancelEdit,
 onEditingNameChange
}: any) {
 return (
 <div className="space-y-6 pb-6">
 {categories.map((category: any) => {
 const categoryTasks = tasks.filter((task: any) => task.category === category.id);
 const isEditing = editingCategoryId === category.id;

 return (
 <div key={category.id} className={`border-2 ${category.borderColor} rounded-lg p-4 ${category.bgColor}`}>
 <div className="flex items-center space-x-3 mb-4">
 <category.icon size={24} className={category.color} />
 <div className="flex-1">
 {isEditing ? (
 <div className="flex items-center space-x-2">
 <Input
 value={editingCategoryName}
 onChange={(e) => onEditingNameChange(e.target.value)}
 className="bg-white/10 border-white/20 text-white text-sm h-8"
 autoFocus
 />
 <Button
 onClick={onSaveEdit}
 size="sm"
 variant="ghost"
 className="p-1 h-8 w-8 text-green-400 hover:bg-green-400/20"
 >
 <Check size={16} />
 </Button>
 <Button
 onClick={onCancelEdit}
 size="sm"
 variant="ghost"
 className="p-1 h-8 w-8 text-red-400 hover:bg-red-400/20"
 >
 <X size={16} />
 </Button>
 </div>
 ) : (
 <div className="flex items-center space-x-2">
 <h3 className={`font-medium ${category.color}`}>{category.name}</h3>
 <Button
 onClick={() => onEditCategory(category.id)}
 size="sm"
 variant="ghost"
 className="p-1 h-6 w-6 text-white/40 hover:text-white/60 hover:bg-white/10"
 >
 <Edit3 size={12} />
 </Button>
 </div>
 )}
 <div className="text-xs text-white/50">
 {categoryTasks.filter((t: any) => t.completed).length}/{categoryTasks.length} completed
 </div>
 </div>
 </div>

 <div className="space-y-3">
 {categoryTasks.map((task: any) => (
 <div key={task.id} className="group">
 <div className="flex items-center space-x-3">
 <Checkbox
 checked={task.completed}
 onCheckedChange={() => onToggleTask(task.id)}
 className={`h-4 w-4 border-white/40 data-[state=checked]:${category.color.replace('text-', 'bg-')} data-[state=checked]:text-black flex-shrink-0`}
 />

 <div className="flex-1 min-w-0">
 <div 
 onClick={() => onTaskClick(task.id)}
 className="cursor-pointer"
 >
 {/* Task title and points in same row to save space */}
 <div className="flex items-center justify-between">
 <span className={`${task.completed ? 'line-through text-white/50' : 'text-white'} text-sm flex-1`}>
 {task.text}
 </span>
 <span className={`${category.color} font-medium text-xs ml-2 flex-shrink-0`}>
 +{task.points} pts
 </span>
 </div>
 </div>
 </div>

 <ChevronRight 
 size={14} 
 className={`text-white/30 opacity-0 group-hover:opacity-100 group-hover:${category.color} transition-all cursor-pointer flex-shrink-0`}
 onClick={() => onTaskClick(task.id)}
 />
 </div>
 </div>
 ))}

 {categoryTasks.length === 0 && (
 <div className="text-center py-4 text-white/40 text-sm">
 No tasks yet. Click the + button to add one.
 </div>
 )}
 </div>
 </div>
 );
 })}
 </div>
 );
}




