+ )
+
+ // Danger Zone
+ DangerZoneCard(
+ onResetPoints = { viewModel.onEvent(SettingsEvent.ResetAllPoints) },
+ onFactoryReset = { viewModel.onEvent(SettingsEvent.FactoryReset) }
+ )
+
+ // Footer
+ SettingsFooter(theme = theme)
