import kotlinx.coroutines.launch

data class App(
 val id: String,
 val name: String,
 val icon: ImageVector,
 val category: String,
 val pointCost: Int,
 val description: String
)

@Composable
fun AppDrawerScreen(
 navController: NavController,
 onClose: () -> Unit = {}
) {
 val coroutineScope = rememberCoroutineScope()
 var countdown by remember { mutableStateOf(60) }
 var isUnlocked by remember { mutableStateOf(false) }
 var selectedApp by remember { mutableStateOf<App?>(null) }
 var showWarning by remember { mutableStateOf(false) }

 val apps = remember {
 listOf(
 // Essential Apps
 App(id = "1", name = "Phone", icon = Icons.Default.Phone, category = "essential", pointCost = 0, description = "Make and receive calls"),
 App(id = "2", name = "Messages", icon = Icons.Default.Message, category = "essential", pointCost = 0, description = "Text messaging"),
