 // Games (Very High Point Cost)
 App(id = "18", name = "Games", icon = Icons.Default.SportsEsports, category = "games", pointCost = 50, description = "Mobile gaming"),

 // Shopping Apps (Medium Point Cost)
 App(id = "19", name = "Shopping", icon = Icons.Default.ShoppingCart, category = "shopping", pointCost = 12, description = "Online shopping"),
 App(id = "20", name = "Food Delivery", icon = Icons.Default.Restaurant, category = "shopping", pointCost = 10, description = "Food ordering"),

 // Additional Apps
 App(id = "21", name = "Chrome", icon = Icons.Default.Public, category = "productivity", pointCost = 3, description = "Web browsing"),
 App(id = "22", name = "Settings", icon = Icons.Default.Settings, category = "essential", pointCost = 0, description = "System settings")
 )
 }

 LaunchedEffect(Unit) {
 while (countdown > 0 && !isUnlocked) {
 delay(1000)
 countdown--
 }
 if (countdown == 0) {
 isUnlocked = true
 }
 }

 val handleAppClick = { app: App ->
 if (app.pointCost > 0) {
 selectedApp = app
 showWarning = true
 } else {
 // Open app (simulation)
 println("Opening ${app.name}")
 onClose()
 }
 }

 val confirmAppOpen = {
 if (selectedApp != null) {
 println("Opening ${selectedApp!!.name} - ${selectedApp!!.pointCost} points will be deducted")
 showWarning = false
 selectedApp = null
 onClose()
 }
 }

 val getCategoryColor = { category: String ->
 when (category) {
 "essential" -> Color(0xFF81C784)
 "productivity" -> Color(0xFF64B5F6)
 "social" -> Color(0xFFE57373)
 "entertainment" -> Color(0xFFFFD54F)
 "games" -> Color(0xFFBA68C8)
 "shopping" -> Color(0xFFFFB74D)
 else -> Color.White.copy(alpha = 0.6f)
 }
 }

 val getPointCostColor = { pointCost: Int ->
