@Composable
fun AppCategoryGrid(
 apps: List<App>,
 handleAppClick: (App) -> Unit,
 rankColors: Map<String, Color>
) {
 LazyVerticalGrid(
 columns = GridCells.Fixed(4),
 horizontalArrangement = Arrangement.spacedBy(12.dp),
 verticalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 items(apps) { app ->
 Column(
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Box(
 modifier = Modifier
 .size(56.dp)
 .background(
 Color.White.copy(alpha = 0.1f),
 RoundedCornerShape(12.dp)
 )
 .border(
 width = 1.dp,
 color = rankColors["border"]!!,
 shape = RoundedCornerShape(12.dp)
 )
 .clickable { handleAppClick(app) },
 contentAlignment = Alignment.Center
 ) {
 Icon(
 imageVector = app.icon,
 contentDescription = app.name,
 tint = Color.White,
 modifier = Modifier.size(24.dp)
 )

 if (app.pointCost > 0) {
 Surface(
 color = when {
 app.pointCost <= 5 -> Color(0xFFFFD54F).copy(alpha = 0.2f)
 app.pointCost <= 15 -> Color(0xFFFFB74D).copy(alpha = 0.2f)
 else -> Color(0xFFE57373).copy(alpha = 0.2f)
 },
 shape = RoundedCornerShape(4.dp),
 border = androidx.compose.foundation.BorderStroke(
 width = 1.dp,
 color = when {
 app.pointCost <= 5 -> Color(0xFFFFD54F).copy(alpha = 0.4f)
 app.pointCost <= 15 -> Color(0xFFFFB74D).copy(alpha = 0.4f)
 else -> Color(0xFFE57373).copy(alpha = 0.4f)
 }
 ),
 modifier = Modifier
 .align(Alignment.TopEnd)
 .offset(x = 6.dp, y = (-6).dp)
 ) {
 Text(
 text = "-${app.pointCost}",
 style = MaterialTheme.typography.labelSmall,
 color = when {
 app.pointCost <= 5 -> Color(0xFFFFD54F)
 app.pointCost <= 15 -> Color(0xFFFFB74D)
 else -> Color(0xFFE57373)
 },
 modifier = Modifier.padding(horizontal = 4.dp, vertical = 2.dp)
 )
 }
 }
 }

 Spacer(modifier = Modifier.height(4.dp))

 Text(
 text = app.name,
 style = MaterialTheme.typography.bodySmall,
 color = Color.White.copy(alpha = 0.8f),
 fontWeight = FontWeight.Medium
 )

 Text(
 text = app.description,
 style = MaterialTheme.typography.labelSmall,
 color = Color.White.copy(alpha = 0.4f)
 )
 }
 }
 }
