'use client';

import { useState } from 'react';
import { Switch } from './ui/switch';
import { Button } from './ui/button';
import { Badge } from './ui/badge';
import { Separator } from './ui/separator';
import { Slider } from './ui/slider';
import { Phone, MessageSquare, Mail, Calendar, Clock, Shield, AlertTriangle } from 'lucide-react';

interface App {
 id: string;
 name: string;
 essential: boolean;
 icon: any;
 category: 'essential' | 'productivity' | 'social' | 'entertainment';
}

interface SettingsScreenProps {
 rankColors?: any;
 hideCompletedTasks?: boolean;
 onHideCompletedTasksChange?: (value: boolean) => void;
}

export function SettingsScreen({ 
 rankColors = { accent: 'text-white', border: 'border-white/20', bg: 'bg-white/10' },
 hideCompletedTasks = true,
 onHideCompletedTasksChange
}: SettingsScreenProps) {
 const [tunnelVisionMode, setTunnelVisionMode] = useState(true);
 const [appDrawerDelay, setAppDrawerDelay] = useState([60]);
 const [distractionAppsDelay, setDistractionAppsDelay] = useState([30]);
 const [pointThreshold, setPointThreshold] = useState([50]);
 const [warningsEnabled, setWarningsEnabled] = useState(true);

 const [apps, setApps] = useState<App[]>([
 { id: '1', name: 'Phone', essential: true, icon: Phone, category: 'essential' },
 { id: '2', name: 'Messages', essential: true, icon: MessageSquare, category: 'essential' },
 { id: '3', name: 'Mail', essential: false, icon: Mail, category: 'productivity' },
 { id: '4', name: 'Calendar', essential: false, icon: Calendar, category: 'productivity' },
 ]);

 const toggleAppEssential = (id: string) => {
 setApps(apps.map(app => 
 app.id === id ? { ...app, essential: !app.essential } : app
 ));
 };

 return (
 <div className="h-full bg-black text-white p-6 overflow-y-auto">
 {/* Enhanced Header */}
 <div className="mb-8">
 <h1 className={`text-xl font-medium mb-2 ${rankColors.accent}`}>CONTROL CENTER</h1>
 <div className="text-xs text-white/60">CONFIGURE YOUR DISCIPLINE SYSTEM</div>
 </div>

 {/* Core Settings */}
 <div className="space-y-6">
 {/* Task Completion Behavior */}
 <div className={`border ${rankColors.border} rounded-lg p-4 ${rankColors.bg}`}>
 <div className="flex items-center justify-between mb-3">
 <div>
 <h3 className={`font-medium ${rankColors.accent}`}>TASK COMPLETION</h3>
 <p className="text-xs text-white/60 mt-1">
 {hideCompletedTasks ? 'Remove completed tasks from view' : 'Show completed tasks as strikethrough'}
 </p>
 </div>
 <Switch
 checked={hideCompletedTasks}
 onCheckedChange={onHideCompletedTasksChange}
 className={`data-[state=checked]:${rankColors.accent.replace('text-', 'bg-')} data-[state=unchecked]:bg-white/20`}
 />
 </div>
 <div className="text-xs text-white/40">
 {hideCompletedTasks 
 ? 'Tasks will be removed and moved to task history' 
 : 'Tasks will remain visible with strikethrough styling'
 }
 </div>
 </div>

 {/* Enhanced Tunnel Vision Mode */}
 <div className={`border ${rankColors.border} rounded-lg p-4 ${rankColors.bg}`}>
 <div className="flex items-center justify-between mb-3">
 <div>
 <h3 className={`font-medium ${rankColors.accent}`}>TUNNEL VISION MODE</h3>
 <p className="text-xs text-white/60 mt-1">Hide all apps except tasks on home screen</p>
 </div>
 <Switch
 checked={tunnelVisionMode}
 onCheckedChange={setTunnelVisionMode}
 className={`data-[state=checked]:${rankColors.accent.replace('text-', 'bg-')} data-[state=unchecked]:bg-white/20`}
 />
 </div>
 {tunnelVisionMode && (
 <Badge variant="outline" className={`text-xs ${rankColors.border} ${rankColors.accent}`}>
 ACTIVE - MAXIMUM FOCUS
 </Badge>
 )}
 </div>

 {/* Enhanced Delay Settings */}
 <div className={`border ${rankColors.border} rounded-lg p-4 space-y-4 ${rankColors.bg}`}>
 <h3 className={`font-medium ${rankColors.accent}`}>ACCESS DELAYS</h3>

 <div>
 <div className="flex justify-between items-center mb-2">
 <span className="text-sm">App Drawer Delay</span>
 <span className={`text-sm ${rankColors.accent}`}>{appDrawerDelay[0]}s</span>
 </div>
 <Slider
 value={appDrawerDelay}
 onValueChange={setAppDrawerDelay}
 max={300}
 min={0}
 step={15}
 className="w-full"
 />
 <p className="text-xs text-white/40 mt-1">Delay before app drawer opens</p>
 </div>

 <Separator className="bg-white/10" />

 <div>
 <div className="flex justify-between items-center mb-2">
 <span className="text-sm">Distraction App Delay</span>
 <span className={`text-sm ${rankColors.accent}`}>{distractionAppsDelay[0]}s</span>
 </div>
 <Slider
 value={distractionAppsDelay}
 onValueChange={setDistractionAppsDelay}
 max={120}
 min={0}
 step={10}
 className="w-full"
 />
 <p className="text-xs text-white/40 mt-1">Additional delay for social/entertainment apps</p>
 </div>
 </div>

 {/* Enhanced Point System */}
 <div className={`border ${rankColors.border} rounded-lg p-4 space-y-4 ${rankColors.bg}`}>
 <h3 className={`font-medium ${rankColors.accent}`}>POINT SYSTEM</h3>

 <div>
 <div className="flex justify-between items-center mb-2">
 <span className="text-sm">Block Threshold</span>
 <span className={`text-sm ${rankColors.accent}`}>{pointThreshold[0]} pts</span>
 </div>
 <Slider
 value={pointThreshold}
 onValueChange={setPointThreshold}
 max={200}
 min={0}
 step={25}
 className="w-full"
 />
 <p className="text-xs text-white/40 mt-1">Apps get blocked below this point level</p>
 </div>

 <div className="flex items-center justify-between">
 <div>
 <span className="text-sm">Distraction Warnings</span>
 <p className="text-xs text-white/60 mt-1">Show warnings when adding distracting apps</p>
