@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun HomeScreen(navController: NavController) {
 val context = LocalContext.current
 val appRepository = remember { AppRepository(context) }
 val taskRepository = remember { TaskRepository() }
 val apps = remember { mutableStateOf<List<AppInfo>>(emptyList()) }
 val tasks = remember { mutableStateOf<List<Task>>(emptyList()) }
 var showAddTaskDialog by remember { mutableStateOf(false) }
 var showEssentialAppsSheet by remember { mutableStateOf(false) }
 val bottomSheetState = rememberModalBottomSheetState(false)
 val coroutineScope = rememberCoroutineScope()

 LaunchedEffect(Unit) {
 apps.value = appRepository.getInstalledApps().filter {
 it.name in listOf("Phone", "Messages") || it.name.contains("App")
 }.take(3)
 }

 LaunchedEffect(taskRepository) {
 tasks.value = taskRepository.getAllTasks()
 }

 Column(
 modifier = Modifier
 .fillMaxSize()
 .background(MaterialTheme.colorScheme.background)
 .padding(top = WindowInsets.statusBars.asPaddingValues().calculateTopPadding()),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 PointsHeader(currentPoints = 245, pointChange = 15, completed = 0, total = 2)
 Spacer(modifier = Modifier.height(8.dp))
 Divider(modifier = Modifier.fillMaxWidth(), color = DividerGray)
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .weight(1f)
 ) {
 TaskList(tasks.value, taskRepository, onTaskCompleted = { id ->
 taskRepository.completeTask(id)
 tasks.value = taskRepository.getAllTasks()
 }, navController)
 }
 AddTaskBar { showAddTaskDialog = true }
 TunnelVisionBanner()
 AppShortcutBar(onAppClick = { appName ->
 if (appName == "Apps") {
 coroutineScope.launch {
 bottomSheetState.show()
 }
 showEssentialAppsSheet = true
 }
 /* TODO: Launch intent */
 })
 // Add bottom padding for navigation bar
 Spacer(modifier = Modifier.height(16.dp))
 }

 if (showEssentialAppsSheet) {
 ModalBottomSheet(
 sheetState = bottomSheetState,
 onDismissRequest = { showEssentialAppsSheet = false },
 containerColor = SurfaceBanner
 ) {
 EssentialAppsSheet(onClose = { showEssentialAppsSheet = false })
 }
 }
 if (showAddTaskDialog) {
 TaskDialog(
 onDismiss = { showAddTaskDialog = false },
 onTaskAdded = { title, desc, pts ->
 taskRepository.addTask(title, desc, pts)
 tasks.value = taskRepository.getAllTasks()
 }
 )
 }
}

