+@Composable
+fun DriveHeader(
+ currentTab: String,
+ onTabSelect: (String) -> Unit,
+ theme: DriveTheme
+) {
+ Column(
+ modifier = Modifier
+ .background(theme.surface)
+ .padding(16.dp)
+ ) {
+ Text(
+ text = "YOUR DRIVE",
+ style = MaterialTheme.typography.titleLarge,
+ color = theme.textPrimary,
+ modifier = Modifier.padding(bottom = 16.dp)
+ )
+
