+data class DriveTheme(
+ val background: Color = Color.Black,
+ val headerBackground: Color = Color(0xFF1F1F1F),
+ val headerTextColor: Color = Color.White,
+ val tabBackground: Color = Color(0xFF1F1F1F),
+ val tabBorderColor: Color = Color.White.copy(alpha = 0.2f),
+ val selectedTabBackground: Color = Color.White,
+ val selectedTabTextColor: Color = Color.Black,
+ val tabTextColor: Color = Color.White,
+ val quoteBackground: Color = Color(0xFF1F1F1F),
+ val quoteBorderColor: Color = Color.White.copy(alpha = 0.2f),
+ val quoteIconColor: Color = Color.White.copy(alpha = 0.6f),
+ val quoteTextColor: Color = Color.White,
+ val quoteAuthorColor: Color = Color.White.copy(alpha = 0.7f),
+ val nextQuoteButtonBackground: Color = Color.Transparent,
+ val nextQuoteButtonTextColor: Color = Color.White,
+ val nextQuoteButtonBorderColor: Color = Color.White.copy(alpha = 0.3f),
+ val categoryBackground: Color = Color(0xFF1F1F1F),
+ val categoryBorderColor: Color = Color.White.copy(alpha = 0.2f),
+ val categoryIconColor: Color = Color.White,
+ val categoryTextColor: Color = Color.White,
+ val categoryQuoteColor: Color = Color.White.copy(alpha = 0.7f),
+ val customQuoteBackground: Color = Color(0xFF1F1F1F),
+ val customQuoteBorderColor: Color = Color.White.copy(alpha = 0.1f),
+ val customQuoteTextColor: Color = Color.White,
+ val customQuoteItemBorderColor: Color = Color.White.copy(alpha = 0.1f),
+ val customQuoteItemTextColor: Color = Color.White,
+ val customQuoteItemAuthorColor: Color = Color.White.copy(alpha = 0.7f),
+ val customQuoteItemDeleteIconColor: Color = Color(0xFFE57373),
+ val customQuoteAddIconColor: Color = Color.White,
+ val visionBoardBackground: Color = Color(0xFF1F1F1F),
+ val visionBoardBorderColor: Color = Color.White.copy(alpha = 0.2f),
+ val visionBoardTextColor: Color = Color.White,
+ val visionBoardAddIconColor: Color = Color.White,
+ val visionCardBackground: Color = Color(0xFF1F1F1F),
